import React, { Component } from 'react';


export default class Conversor extends Component {

    constructor(props) {
        super(props);

        this.state = {
            moedaA_valor:"",
            moedaB_valor:0
        }
    }

    render() {
        return (
            <div className="conversor">
                <h1>{this.props.moedaA} para {this.props.moedaB}</h1>
                <input type="text" onChange={(event)=>{this.setState(moedaA_valor)}}></input>
                <input type="button" value="Coverter"></input>
                <h2>Valor convertido</h2>
            </div>
        )
    }
}
